sys_script.script:
// It checks if password meets Active Directory requirements if not sends an email
// with a correct password using an event to fire a notification and includes the current
// object, email and password to use it in notification.

(function executeRule(current, previous /*null when async*/ ) {

    // Checks if password meets AD requirements
    if (!current.user_password.match(/^(?=.*?[a-z])(?=.*?[A-Z])(?=.*?[0-9])(?=.*?[#$%^&*()\-_+={}[\]|\:;"'<>,.?\/]).{8,14}$/)) {

        // Shows a message to notify user about sending the email 
        gs.addErrorMessage("The password was incorrect a default password was send to your mail instead");

        // Assign a correct password
        current.user_password = "Th|s0nePass";

        try {
            // Uses eventQueue to start the event that fires the notification
            // It sends the current object, the email and password to notification to use it
            gs.eventQueue("x_58872_needit.new.password.email", current, current.email.getDisplayValue(), current.user_password);
        } catch (err) {
            gs.info("Event Q error: " + err);
        }
        // Especifies that the password is temporary and needs to be reset after login
        current.password_needs_reset = true;

    } else {
        gs.info("Password matches");
    }

})(current, previous);
