sys_script_include.script:
// Class to obtain cross referenced data from server to client about company
var GetCompanyData = Class.create();

// Object inheritance from global.AbstractAjaxProcessor
GetCompanyData.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// Obtains country from based on company's field that is on client side
	getCountry: function() {
		// GlideRecord object from core_company
		var companyRecord = new GlideRecord("core_company");
		try{
			// Obtaining record based on company's sys_id parameter sendend by client
			companyRecord.get(this.getParameter('sysparm_companyID'));

		}catch(err){
			gs.info("Error " + err);
		}

		// Sends back country from the record
		return companyRecord.country + '';

	},

	// Obtains phone number based on company's field that is on client side
	getPhone: function() {
		// GlideRecord object from core_company
		var companyRecord = new GlideRecord("core_company");
		try{
			// Obtaining record using company's sys_id parameter sendend by client
			companyRecord.get(this.getParameter('sysparm_companyID'));

		}catch(err){
			gs.info("Error " + err);
		}

		// Send back country from the record
		return companyRecord.phone + '';

	},


    type: 'GetCompanyData'
});
